$(function(){function timer(timeStr,item){setInterval(function(){var nowTime=new Date(timeStr)-new Date;var minutes=parseInt(nowTime/1000/60%60,10);var seconds=parseInt(nowTime/1000%60,10);minutes=checkTime(minutes);seconds=checkTime(seconds);if(item==="day"){var days=parseInt(nowTime/1000/60/60/24,10);var hours=parseInt(nowTime/1000/60/60%24,10);days=checkTime(days);hours=checkTime(hours);$("#count_day").text(days);$("#count_hour").text(hours);$("#count_minute").text(minutes);$("#count_second").text(seconds)}else{var hours=parseInt(nowTime/(1000*60*60),10);hours=checkTime(hours);$("#count_hour").text(hours);$("#count_minute").text(minutes);$("#count_second").text(seconds)}},1000)}function checkTime(i){if(i<10){i="0"+i}return i}timer("2018-12-20 00:00:00","day")});